"""This is my program to check a 16 digit number as a valid credit card number"""
def check_card(number):
    # number = str(number)
    # number = "4556737586899855"
    # print(type(number),number)
    card = list(number)
    # print(card)
    """turning credit card number into list (string, not ints)"""
    number = ''.join(number)
    # print(f"=========================================\nCredit Card number: {number}, checking...")
    check1 = card.pop()
    # print(check1)
    """grabbed the last number to use as check then reversed order"""
    card = card[::-1]
    # print(card)
    """took the number and reversed it"""
    card = (''.join(card))
    # print(check1)
  
    # print(f"""
    # this is the card: {number} 
    # Correct Format: {type(card)==str}""")
    card = list(card)
    mult = card[0::2]
    nonmult = card[1::2]
    # print(nonmult)
    for num in mult:
        num = int(num)*2
        # print(f"beginning of for loop{num}")
        if num > 9:
            num -= 9
        # print(f"before append{nonmult}")    
        nonmult.append(num)
        # print(f"after append{nonmult}")
    # print(mult)    
    # print(nonmult)
    sum = 0
    for num in nonmult:
        sum += int(num)
    # print(f"sum:{sum}")    
    sum = list(str(sum))
    check2 = sum[1]
    # print(f"""    \tcheck1: {check1}
    # \tcheck2: {check2}""")
    if check2 == check1:
        # print(f"{number} check1:{check1} check2:{check2}")
        return "Authenticated"
    else:
        # print(f"{number} check1:{check1} check2:{check2}")
        return "Invalid number!"

